// makeTxWithValue creates a transaction invoking addr, sending eth along with calldata.
func makeTxWithValue(addr common.Address, value *big.Int, data []byte) *types.Transaction {
	ctx := context.Background()
	cl, sk := helper.GetRealBackend()
	backend := ethclient.NewClient(cl)
	sender := common.HexToAddress(txfuzz.ADDR)
	nonce, err := backend.PendingNonceAt(ctx, sender)
	if err != nil {
		panic(err)
	}
	chainid, err := backend.ChainID(ctx)
	if err != nil {
		panic(err)
	}
	fmt.Printf("Nonce: %v\n", nonce)
	gp, err := backend.SuggestGasPrice(ctx)
	if err != nil {
		panic(err)
	}
	tip, err := backend.SuggestGasTipCap(ctx)
	if err != nil {
		panic(err)
	}
	return types.MustSignNewTx(sk, types.NewCancunSigner(chainid), &types.DynamicFeeTx{
		ChainID:   chainid,
		Nonce:     nonce,
		GasTipCap: tip,
		GasFeeCap: gp,
		Gas:       200_000,
		Value:     value,
		To:        &addr,
		Data:      data,
	})
}

func test7702() {
	slog.Info("EIP-7702")
	proxy, err := deploy7702Proxy()
	if err != nil {
		panic(err)
	}
	do7702Calls(proxy)
	selfAddr := common.HexToAddress(txfuzz.ADDR)
	do7702Calls(selfAddr)

	for i := 0; i < 18; i++ {
		prec, err := deployPrecompileCaller(fmt.Sprintf("%02x", i))
		if err != nil {
			panic(err)
		}
		unsigned := types.SetCodeAuthorization{
			ChainID: *uint256.MustFromBig(helper.ChainID()),
			Address: selfAddr,
			Nonce:   helper.Nonce(selfAddr),
		}
		sk := crypto.ToECDSAUnsafe(common.FromHex(txfuzz.SK))
		self, _ := types.SignSetCode(sk, unsigned)
		helper.Wait(helper.ExecAuth(prec, []byte{0x01}, []types.SetCodeAuthorization{self}))
	}
	// Clear the authorization
	empty := types.SetCodeAuthorization{
		ChainID: *uint256.MustFromBig(helper.ChainID()),
		Address: common.Address{},
		Nonce:   helper.Nonce(selfAddr),
	}
	sk := crypto.ToECDSAUnsafe(common.FromHex(txfuzz.SK))
	emptyAuth, _ := types.SignSetCode(sk, empty)
	helper.Wait(helper.ExecAuth(selfAddr, []byte{}, []types.SetCodeAuthorization{emptyAuth}))
}

func do7702Calls(addr common.Address) {
	// authenticate self
	selfAddr := common.HexToAddress(txfuzz.ADDR)
	unsigned := types.SetCodeAuthorization{
		ChainID: *uint256.MustFromBig(helper.ChainID()),
		Address: selfAddr,
		Nonce:   helper.Nonce(selfAddr),
	}
	sk := crypto.ToECDSAUnsafe(common.FromHex(txfuzz.SK))
	self, _ := types.SignSetCode(sk, unsigned)
	helper.Wait(helper.ExecAuth(addr, []byte{}, []types.SetCodeAuthorization{self}))
	// authenticate self twice
	helper.Wait(helper.ExecAuth(addr, []byte{}, []types.SetCodeAuthorization{self, self}))
	// authenticate self twice with different nonces
	self2 := self
	self2.Nonce = helper.Nonce(addr) + 1
	self2P, _ := types.SignSetCode(sk, self2)
	helper.Wait(helper.ExecAuth(addr, []byte{}, []types.SetCodeAuthorization{self, self2P}))
	// unsigned authorization
	helper.Wait(helper.ExecAuth(addr, []byte{}, []types.SetCodeAuthorization{unsigned}))
	// many authorizations
	var list []types.SetCodeAuthorization
	for i := 0; i < 1024; i++ {
		list = append(list, self)
	}
	helper.Wait(helper.ExecAuth(addr, []byte{}, list))
	// too many authorizations
	for i := 0; i < 1024*1023; i++ {
		list = append(list, self)
	}
	helper.ExecAuth(addr, []byte{}, list)
}

func test2935() {
	slog.Info("EIP-2935")
	contr, err := deploy2935Caller()
	if err != nil {
		panic(err)
	}
	addresses := []common.Address{
		contr,
		params.HistoryStorageAddress,
	}

	cl, _ := helper.GetRealBackend()

	for _, addr := range addresses {
		// empty bytes
		helper.Exec(addr, []byte{}, false)
		// 32 bytes random
		var randomBytes [32]byte
		rand.Read(randomBytes[:])
		helper.Exec(addr, randomBytes[:], false)
		// 33 bytes
		var bigBytes [33]byte
		rand.Read(bigBytes[:])
		helper.Exec(addr, bigBytes[:], false)
		// 32 bytes 0
		var zeroBytes [32]byte
		helper.Exec(addr, zeroBytes[:], false)
		// 1
		helper.Exec(addr, []byte{1}, false)
		// block number specifics
		client := ethclient.NewClient(cl)
		currentBlock, err := client.BlockNumber(context.Background())
		if err != nil {
			panic(err)
		}
		// current block number
		blocknumbers := []uint64{
			currentBlock,
			currentBlock + 1,
			currentBlock - 1,
			currentBlock - 8192,
			currentBlock - 256,
			currentBlock - 255,
			currentBlock - 20000,
		}
		for _, number := range blocknumbers {
			input := make([]byte, 32)
			binary.BigEndian.PutUint64(input[24:], number)
			helper.Exec(addr, input, false)
		}
	}
}
